const initSliders = () => {
    // Get all slider wrappers on the page
    const sliderWrappers = document.querySelectorAll(".slider-wrapper");
    
    sliderWrappers.forEach(sliderWrapper => {
        const imageList = sliderWrapper.querySelector(".image-list");
        const slideButtons = sliderWrapper.querySelectorAll(".slide-button");
        const maxScrollLeft = imageList.scrollWidth - imageList.clientWidth;
        
        // Slide product cards according to the button clicked
        slideButtons.forEach(button => {
            button.addEventListener("click", () => {
                const direction = button.id.includes("prev-slide") ? -1 : 1;
                const scrollAmount = imageList.clientWidth * direction;
                imageList.scrollBy({left: scrollAmount, behavior: "smooth"});
            });
        });

        const handleSlideButtons = () => {
            slideButtons[0].style.display = imageList.scrollLeft <= 0 ? "none" : "block";
            slideButtons[1].style.display = imageList.scrollLeft >= maxScrollLeft ? "none" : "block";
        }

        imageList.addEventListener("scroll", () => {
            handleSlideButtons();
        });

        // Initialize button visibility
        handleSlideButtons();
    });
}

window.addEventListener("load", initSliders);